{
  "id": "webhook-to-db",
  "name": "Webhook to Database",
  "description": "Receives webhook data and stores it in PostgreSQL",
  "nodes": [
    {
      "id": "trigger_1",
      "type": "webhook",
      "name": "Receive Webhook",
      "position": {"x": 100, "y": 200},
      "config": {
        "method": "POST",
        "path": "/webhook/data",
        "authentication": "none"
      },
      "outputs": ["output_main"]
    },
    {
      "id": "action_1",
      "type": "database",
      "name": "Insert Record",
      "position": {"x": 400, "y": 200},
      "config": {
        "operation": "insert",
        "table": "webhook_data",
        "columns": ["payload", "received_at"]
      },
      "inputs": ["input_main"],
      "outputs": ["output_main"]
    }
  ],
  "edges": [
    {
      "id": "edge_1",
      "source": "trigger_1",
      "target": "action_1",
      "sourceHandle": "output_main",
      "targetHandle": "input_main"
    }
  ]
}

{
  "id": "api-sync-hourly",
  "name": "Hourly API Sync",
  "description": "Syncs data between source and destination APIs every hour",
  "nodes": [
    {
      "id": "trigger_1",
      "type": "schedule",
      "name": "Hourly Trigger",
      "position": {"x": 100, "y": 200},
      "config": {
        "cron": "0 * * * *",
        "timezone": "UTC"
      },
      "outputs": ["output_main"]
    },
    {
      "id": "action_1",
      "type": "http_request",
      "name": "Fetch Source Data",
      "position": {"x": 350, "y": 200},
      "config": {
        "method": "GET",
        "url": "{{$env.SOURCE_API_URL}}/data",
        "headers": {"Authorization": "Bearer {{$env.SOURCE_API_KEY}}"}
      },
      "inputs": ["input_main"],
      "outputs": ["output_main"]
    },
    {
      "id": "logic_1",
      "type": "transform",
      "name": "Map Data",
      "position": {"x": 600, "y": 200},
      "config": {
        "expression": "items.map(item => ({id: item.id, name: item.title, updated: new Date()}))"
      },
      "inputs": ["input_main"],
      "outputs": ["output_main"]
    },
    {
      "id": "action_2",
      "type": "http_request",
      "name": "Push to Destination",
      "position": {"x": 850, "y": 200},
      "config": {
        "method": "POST",
        "url": "{{$env.DEST_API_URL}}/sync",
        "headers": {"Authorization": "Bearer {{$env.DEST_API_KEY}}"},
        "body": "{{$json}}"
      },
      "inputs": ["input_main"],
      "outputs": ["output_main"]
    }
  ],
  "edges": [
    {"id": "e1", "source": "trigger_1", "target": "action_1"},
    {"id": "e2", "source": "action_1", "target": "logic_1"},
    {"id": "e3", "source": "logic_1", "target": "action_2"}
  ]
}

{
  "id": "order-routing",
  "name": "Order Priority Routing",
  "description": "Routes orders based on value threshold",
  "nodes": [
    {
      "id": "trigger_1",
      "type": "webhook",
      "name": "New Order",
      "position": {"x": 100, "y": 300},
      "config": {"method": "POST", "path": "/orders/new"},
      "outputs": ["output_main"]
    },
    {
      "id": "logic_1",
      "type": "if",
      "name": "Check Order Value",
      "position": {"x": 350, "y": 300},
      "config": {
        "conditions": [{"field": "order.total", "operator": "greaterThan", "value": 1000}]
      },
      "inputs": ["input_main"],
      "outputs": ["output_true", "output_false"]
    },
    {
      "id": "action_1",
      "type": "message_queue",
      "name": "Priority Queue",
      "position": {"x": 600, "y": 200},
      "config": {"queue": "orders.priority", "operation": "publish"},
      "inputs": ["input_main"]
    },
    {
      "id": "action_2",
      "type": "message_queue",
      "name": "Standard Queue",
      "position": {"x": 600, "y": 400},
      "config": {"queue": "orders.standard", "operation": "publish"},
      "inputs": ["input_main"]
    }
  ],
  "edges": [
    {"id": "e1", "source": "trigger_1", "target": "logic_1"},
    {"id": "e2", "source": "logic_1", "sourceHandle": "output_true", "target": "action_1"},
    {"id": "e3", "source": "logic_1", "sourceHandle": "output_false", "target": "action_2"}
  ]
}
